import React, { useEffect, useState } from 'react';
import UserService from '../services/UserService';

function UserManagement() {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ username: '', password: '', role: '', entite: '' });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = () => {
    UserService.getAllUsers()
      .then(response => setUsers(response.data))
      .catch(error => console.error("Erreur lors de la rÃ©cupÃ©ration des utilisateurs :", error));
  };

  const handleAddUser = () => {
    if (Object.values(newUser).some(field => !field)) {
      alert("Tous les champs sont obligatoires !");
      return;
    }
    UserService.createUser(newUser)
      .then(() => {
        setNewUser({ username: '', password: '', role: '', entite: '' });
        fetchUsers();
      })
      .catch(error => console.error("Erreur lors de l'ajout de l'utilisateur :", error));
  };

  const handleDeleteUser = (id) => {
    UserService.deleteUser(id)
      .then(fetchUsers)
      .catch(error => console.error("Erreur lors de la suppression de l'utilisateur :", error));
  };

  return (
<div className="card p-3 mb-3">
  <h5 className="card-title">Ajouter un utilisateur</h5>
  <div className="row g-2">
    <div className="col-md-3">
      <input
        type="text"
        placeholder="Nom d'utilisateur"
        value={newUser.username}
        onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
        className="form-control"
      />
    </div>
    <div className="col-md-3">
      <input
        type="password"
        placeholder="Mot de passe"
        value={newUser.password}
        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
        className="form-control"
      />
    </div>
    
    {/* SÃ©lection de l'entitÃ© */}
    <div className="col-md-3">
      <label className="form-label">EntitÃ©</label>
      <select
        className="form-select"
        value={newUser.entite}
        onChange={(e) => setNewUser({ ...newUser, entite: e.target.value })}
      >
        <option value="">SÃ©lectionner une entitÃ©</option>
        <option value="DPF">DPF</option>
        <option value="DGR">DGR</option>
        <option value="DA">DA</option>
        <option value="DDA">DDA</option>
        <option value="DRH">DRH</option>
        <option value="SAICG">SAICG</option>
        <option value="SMG">SMG</option>
      </select>
    </div>

    {/* SÃ©lection du rÃ´le */}
    <div className="col-md-3">
      <label className="form-label">RÃ´le</label>
      <select
        className="form-select"
        value={newUser.role}
        onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
      >
        <option value="">SÃ©lectionner un rÃ´le</option>
        <option value="admin">Admin</option>
        <option value="superadmin">Super Admin</option>
        <option value="usernormale">Utilisateur Normal</option>
      </select>
    </div>

    <div className="col-md-3">
      <button onClick={handleAddUser} className="btn btn-success w-100 mt-3">Ajouter</button>
    </div>
  </div>
</div>

  );
}

export default UserManagement;
