import React from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';

const HeaderComponent = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const history = useHistory();
    const location = useLocation();

    // Obtenir l'URL actuelle
    const handleLogout = () => {
        localStorage.removeItem('user'); // Supprimer l'utilisateur de localStorage
        history.push('/login'); // Rediriger vers la page de connexion
    };

    return (
        <div>
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-primary px-4 py-3 shadow-sm">
                    <div className="container-fluid d-flex justify-content-between align-items-center">
                        {/* Logo ou titre */}
                        <a href="#" className="navbar-brand fw-bold text-white">
                            Application d'Appels d'Offres
                        </a>

                        {/* Liens de navigation */}
                        <div className="d-flex align-items-center">
                            {user && location.pathname !== '/login' && user.role === 'admin' && (
                                <>
                                    <Link className="nav-link text-white mx-2" to="/dashboard">
                                        <strong>RECAP</strong>
                                    </Link>
                                    <Link className="nav-link text-white mx-2" to="/appelOffres">
                                        <strong>Liste des Appels d'Offres</strong>
                                    </Link>
                                </>
                            )}
                        </div>

                        {/* Bouton de connexion ou déconnexion */}
                        <div className="d-flex align-items-center">
                            {user ? (
                                <>
                                    {/* Logo utilisateur */}
                                    <i className="bi bi-person-circle text-white mx-2" style={{ fontSize: '1.8rem' }}></i>
                                    {/* Nom de l'utilisateur */}
                                    <span className="text-white mx-2">{user.role}</span>
                                    {/* Bouton déconnexion */}
                                    <button
                                        className="btn btn-outline-light mx-2 px-4 py-2"
                                        onClick={handleLogout}
                                    >
                                        Déconnexion
                                    </button>
                                </>
                            ) : (
                                <Link to="/login" className="btn btn-outline-light px-4 py-2">
                                    Connexion
                                </Link>
                            )}
                        </div>
                    </div>
                </nav>
            </header>

            <br /><br />
        </div>
    );
}

export default HeaderComponent;
