import axios from 'axios';
import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';

const LoginComponent = () => {



    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const history = useHistory(); // Hook pour naviguer

    
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response =  axios.post('http://localhost:8080/auth/login', { username, password });
            setMessage(response.data); // Afficher un message de succès
            localStorage.setItem('user', username); // Stocker l'utilisateur localement
            history.push('/appelOffres');
        } catch (err) {
            setMessage(err.response?.data || 'Erreur lors de la connexion');
        }
    };

  return (
    <div className="login-container">
    <h2>Connexion</h2>
    <form onSubmit={handleSubmit}>
        <div className="form-group">
            <label>Nom d'utilisateur</label>
            <input
                type="text"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
            />
        </div>
        <div className="form-group">
            <label>Mot de passe</label>
            <input
                type="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
        </div>
        <button type="submit" className="btn btn-primary">Se connecter</button>
    </form>
    {message && <p>{message}</p>}
</div>
  )
}

export default LoginComponent
