import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const HeaderComponent = () => {
    const user = JSON.parse(localStorage.getItem('user') || "null");
    const navigate = useNavigate();
    const location = useLocation();

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/login');
    };

    const adminLinks = (
        <>
            <Link className="nav-link text-white mx-2" to="/dashboard">Recap</Link>
            <Link className="nav-link text-white mx-2" to="/appelOffres">Liste des Appels d'Offres</Link>
        </>
    );

    return (
        <header>
            <nav className="navbar navbar-expand-md navbar-dark bg-dark px-3">
                <div className="container-fluid d-flex justify-content-between align-items-center">
                    {/* Logo ou titre */}
                    <Link to="/" className="navbar-brand">
                        Appel d'Offre Application
                    </Link>

                    {/* Liens de navigation */}
                    <div className="d-flex align-items-center">
                        {user && location.pathname !== '/login' && user.role === 'admin' && adminLinks}
                    </div>

                    {/* Bouton de connexion et logo utilisateur */}
                    <div className="d-flex align-items-center">
                        {user ? (
                            <div className="d-flex align-items-center">
                                {/* Logo utilisateur */}
                                <i className="bi bi-person-circle text-white mx-2" style={{ fontSize: '1.5rem' }}></i>
                                {/* Nom de l'utilisateur */}
                                <span className="text-white mx-2">{user.role}</span>
                                {/* Bouton déconnexion */}
                                <button
                                    className="btn btn-outline-light mx-2"
                                    onClick={handleLogout}
                                >
                                    Déconnexion
                                </button>
                            </div>
                        ) : (
                            <a href="/login" className="btn btn-outline-light">
                                Connexion
                            </a>
                        )}
                    </div>
                </div>
            </nav>
        </header>
    );
};

export default HeaderComponent;
