import React from 'react'
import { Link ,useHistory} from 'react-router-dom'
const HeaderComponent = () => {

    let user = null;
    try {
        const userString = localStorage.getItem('user');
        user = userString ? JSON.parse(userString) : null;
    } catch (error) {
        console.error('Données JSON invalides détectées dans localStorage:', error);
        localStorage.removeItem('user'); // Nettoyez si les données sont invalides
    }
    const history = useHistory();

    const handleLogout = () => {
        localStorage.removeItem('user'); // Supprimer l'utilisateur de localStorage
        history.push('/login'); // Rediriger vers la page de connexion
    };

    return (
        <div>
        <header>
            <nav className="navbar navbar-expand-md navbar-dark bg-dark px-3">
                <div className="container-fluid d-flex justify-content-between align-items-center">
                    {/* Logo ou titre */}
                    <a href="#" className="navbar-brand">
                        Appel d'Offre Application {user.role}
                    </a>

                    {/* Liens de navigation */}
                    <div className="d-flex align-items-center">
                    {user && (
                                <>
                                    <Link className="nav-link text-white mx-2" to="/dashboard">
                                        Tableau de Bord
                                    </Link>
                                    <Link className="nav-link text-white mx-2" to="/appelOffres">
                                        Liste des Appels d'Offres
                                    </Link>
                                </>
                            )}                      
                    </div>

                    {/* Bouton de connexion */}
                    <div>
                    {user ? (
                                <button
                                    className="btn btn-outline-light"
                                    onClick={handleLogout}
                                >
                                    Déconnexion
                                </button>
                            ) : (
                                <a href="/login" className="btn btn-outline-light">
                                    Connexion
                                </a>
                            )}
                    </div>
                </div>
            </nav>
        </header>
            <br></br><br></br>
        </div>
    )
}

export default HeaderComponent
