import React, {useState, useEffect} from 'react'
import AppelOffreService from '../services/AppelOffreService'
import { Link } from 'react-router-dom'

const ListAppelOffreComponent = () => {

const [appelOffre, setAppelOffre] = useState([])


  useEffect(() => {

        getAllAppelOffre();
    }, [])

    const getAllAppelOffre = () => {
        AppelOffreService.getAllAppelOffre().then((response) => {
            setAppelOffre(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    } 
  return (
    <div className="container">
           
    <h2 className="texte-center">List AppelOffre</h2>
    <Link to = "/add-appeloffre" className = "btn btn-primary mb-2" > Add appelOffre </Link>
    <table className="table table-bordered table-striped">
        <thead>
                <tr>
                    <th>N°</th>
                    <th>Entité</th>
                    <th>Objet</th>
                    <th>Type Marché</th>
                    <th>Estimation</th>
                    <th>PME</th>
                    <th>Date Ouverture Prévisionnelle</th>
                    <th>Date Jugement</th>
                    <th>Observations</th>
                </tr>
            </thead>

            <tbody>
                {appelOffre.map(appel => (
                    <tr key={appel.id}>
                        <td>{appel.id}</td>
                        <td>{appel.entite}</td>
                        <td>{appel.objet}</td>
                        <td>{appel.typeMarche}</td>
                        <td>{appel.estimation}</td>
                        <td>{appel.pme}</td>
                        <td>{appel.dateOuverturePrevisionnelle}</td>
                        <td>{appel.dateJugement}</td>
                        <td>{appel.observations}</td>
                    </tr>
                ))}
            </tbody>
        </table>

    </div>
  )
}

export default ListAppelOffreComponent
