import React, { useState } from 'react';
import './UserManagementCustom.css'; // Fichier CSS séparé

const UserManagement = () => {
  const [newUser, setNewUser] = useState({ username: '', password: '', role: '', entite: '' });
  const [users, setUsers] = useState([]);

  const handleAddUser = () => {
    const id = users.length + 1;
    setUsers([...users, { id, ...newUser }]);
    setNewUser({ username: '', password: '', role: '', entite: '' });
  };

  const handleDeleteUser = (id) => {
    setUsers(users.filter((user) => user.id !== id));
  };

  return (
    <div className="user-management-custom">
      <h2 className="title-custom">Gestion des Utilisateurs</h2>

      {/* Formulaire d'ajout utilisateur */}
      <div className="form-container-custom">
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          value={newUser.username}
          onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
          className="form-input-custom"
        />
        <input
          type="password"
          placeholder="Mot de passe"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          className="form-input-custom"
        />
        <select
          value={newUser.role}
          onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
          className="form-select-custom"
        >
          <option value="">Sélectionner un rôle</option>
          <option value="admin">Admin</option>
          <option value="soadmiij">Super Admin</option>
          <option value="usernormale">Utilisateur Normal</option>
        </select>
        <select
          value={newUser.entite}
          onChange={(e) => setNewUser({ ...newUser, entite: e.target.value })}
          className="form-select-custom"
        >
          <option value="">Sélectionner une entité</option>
          <option value="DPF">DPF</option>
          <option value="DGR">DGR</option>
          <option value="DA">DA</option>
          <option value="DDA">DDA</option>
          <option value="DRH">DRH</option>
          <option value="SAICG">SAICG</option>
          <option value="SMG">SMG</option>
        </select>
        <button onClick={handleAddUser} className="btn-add-custom">Ajouter</button>
      </div>

      {/* Tableau des utilisateurs */}
      <table className="user-table-custom">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom Utilisateur</th>
            <th>Rôle</th>
            <th>Entité</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.role}</td>
              <td>{user.entite}</td>
              <td>
                <button onClick={() => handleDeleteUser(user.id)} className="btn-delete-custom">Supprimer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserManagement;
