import React, {useState, useEffect} from 'react'
import AppelOffreService from '../services/AppelOffreService'
import { Link ,useHistory} from 'react-router-dom'

const ListAppelOffreComponent = () => {

const [entiteF, setEntiteF] = useState('')
const [typeMarcheF, settypeMarcheF] = useState('')

const [appelOffre, setAppelOffre] = useState([])


  useEffect(() => {
    
        getAllAppelOffre(entiteF,typeMarcheF);
        
    }, [entiteF, typeMarcheF])

    const getAllAppelOffre = (entiteF,typeMarcheF) => {
        AppelOffreService.getAllAppelOffre(entiteF,typeMarcheF).then((response) => {
            setAppelOffre(response.data)
            console.log(entiteF);
            console.log(typeMarcheF);
        }).catch(error =>{
            console.log(error);
        })
    } 

   const deleteappelOffre = (appelOffreId) => {
       AppelOffreService.deleteappelOffre(appelOffreId).then((response) =>{
        getAllAppelOffre();

       }).catch(error =>{
           console.log(error);
       })
        
    }
    const isValidDate = (date) => {
        // VÃ©rifie si la date est valide
        if(date!=null){
            return true;
        }else{
            return false;
        }
      };

      const history = useHistory();


    //   const fetchAppelOffres = (e) => {
    //     e.preventDefault();     
    //     getAllAppelOffre(entiteF,typeMarcheF);
    //             console.log(entiteF)
                  
        
    // }

  return (

    
    <div className="container">
           
    <h2 className="texte-center">List AppelOffre</h2>
   
               {/*  */}
               <div>
               <div class="row">
    <div class="col-5">
    <select class="form-select" 
                         value = {entiteF}
                         onChange = {(e) => setEntiteF(e.target.value)}                             
               >
                <option selected>ENTITE</option>
                <option value="DPF">DPF</option>
                <option value="DGR">DGR</option>
                <option value="DA">DA</option>
                <option value="DDA">DDA</option>
                <option value="DDDA">DDDA</option>
                <option value="DRH">DRH</option>
                <option value="SAICG">SAICG</option>
                <option value="SAICG">SAICG</option>
                </select>
    </div>
    <div class="col-5">
    <select class="form-select" 
                        value = {typeMarcheF}
                        onChange = {(e) => settypeMarcheF(e.target.value)}                       
               >
                <option selected>TYPE  MARCHE</option>
                <option value="F">F</option>
                <option value="S">S</option>
                <option value="T">T</option>
                </select>
    </div>
    <div class="col">
    <Link to = "/add-appeloffre" className = "btn btn-primary mb-2" > Add appelOffre </Link>
    </div>
  </div>



        {/* <input
          type="text"
          name="entite"
          placeholder="Filtrer par EntitÃ©"
          value = {entiteF}
          onChange = {(e) => setEntiteF(e.target.value)}
        /> */}
        {/* <input
          type="text"
          name="typeMarche"
          placeholder="Filtrer par Type de MarchÃ©"
          value = {typeMarcheF}
          onChange = {(e) => settypeMarcheF(e.target.value)}
        /> */}
{/* <button className = "btn btn-success" onClick = {(e) => fetchAppelOffres(e)} >Submit </button> */}
      </div>
            {/*  */}
    {/* <div class="table-responsive"> */}
    <table className="table table-bordered table-striped">
        <thead>
                <tr>
                    <th>NÂ°</th>
                    <th>EntitÃ©</th>
                    <th>Objet</th>
                    <th>Type MarchÃ©</th>
                    <th>Estimation</th>
                    <th>PME</th>
                    {/*  */}
                    <th>Date de publication Pr</th>
                    <th>DATE DâOUVERTURE Pr</th>
                    {/*  */}
                    <th>Transmis   CE</th>
                    <th> Observation MC </th>
                  
                    <th> Date ouverture R </th>
                    <th>Date jugement</th>
                    <th> OSERVATIONS </th>
                    <th> Actions </th>
                </tr>
            </thead>

            <tbody>
                {appelOffre.map(appel => (
                    <tr key={appel.id}            style={{
                        backgroundColor: isValidDate(appel.dateOuvertureReelle)
                          ? "green"
                          : "white",
                      }} >
                        <td>{appel.numero}</td>
                        <td>{appel.entite}</td>
                        <td>{appel.objet}</td>
                        <td>{appel.typeMarche}</td>
                        <td>{appel.estimation}</td>
                        <td>{appel.pme}</td>
                        {/*  */}
                        <td>{appel.moisPublicationPrevisionnelle}</td>
                        <td>{appel.dateOuverturePrevisionnelle}</td>
                        {/*  */}
                        <td>{appel.datetransmisCe}</td>
                        <td>{appel.dateobservationMc}</td>
                        <td>{appel.dateOuvertureReelle}</td>
                        <td>{appel.dateJugement}</td>
                        <td>{appel.observations}</td>
                        <td>
                                    <Link className="btn btn-info " style = {{marginLeft:"10px" , fontSize:"10px"}}  to={`/edit-employee/${appel.id}`} >Update</Link>
                                    <button className = "btn btn-danger" onClick = {() => deleteappelOffre(appel.id)}
                                    style = {{marginLeft:"20px"}}> Delete</button>
                                </td>
                    </tr>
                ))}
            </tbody>
        </table>
    {/* </div> */}
    </div>
  )
}




export default ListAppelOffreComponent
